import React, { Component } from 'react'
import Task from '../components/Task'

class TaskContainer extends Component {

    tasks = () => {
        return this.props.tasks.map(task => <Task key={task.id} task={task} />)
    }

    render() {
        return (
            <div class="tasks">
                <h5>Tasks</h5>
                {this.tasks()}
            </div>
        )
    }
}

export default TaskContainer
