import React, { Component } from 'react' 

class Button extends Component {
    state = {
        selected: ""
    }

    clickHandler = () => {
        this.setState({selected: 'selected'})
    }

    render() {
        return <button class={this.state.selected} onClick={this.props.clickHandler}>{this.props.cat}</button>
    }
}

export default Button
