import React from 'react';
import './App.css';
import CategoryContainer from './Containers/CategoryContainer';
import TaskContainer from './Containers/TaskContainer';
import { CATEGORIES } from './data';

class App extends React.Component {
  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food',
      },
      {
        text: 'Save a tenner',
        category: 'Money',
      },
      {
        text: 'Build a todo app',
        category: 'Code',
      },
      {
        text: 'Build todo API',
        category: 'Code',
      },
      {
        text: 'Get an ISA',
        category: 'Money',
      },
      {
        text: 'Cook rice',
        category: 'Food',
      },
      {
        text: 'Tidy house',
        category: 'Misc',
      },
    ],
    selectedButton: '',
  };

  buttonSelector = (e) => {
    console.log(this.state.selectedButton === e.target.name);
    e.persist();
    this.setState(() => ({
      selectedButton: e.target.textContent,
    }));
    if (this.state.selectedButton === e.target.textContent) {
      e.target.className = 'selected';
    } else {
      e.target.className = '';
    }
  };

  categoryFilter = (e) => {
    console.log(e.target);
    // const newTasksArray = [...this.state.tasks];
    // const filteredArray = newTasksArray.filter(
    //   (task) => task.category === e.target.textContent
    // );
    // return filteredArray;
  };

  render() {
    console.log(this.state.selectedButton);
    return (
      <div className='App'>
        <h2>My tasks</h2>
        <CategoryContainer
          categories={CATEGORIES}
          buttonSelector={this.buttonSelector}
          selectedButton={this.state.selectedButton}
        />
        <TaskContainer tasks={this.state.tasks} />
      </div>
    );
  }
}

export default App;
