import React, { Component } from 'react' 

class Form extends Component {

    state = {
        text: ''
    }

    render() {
        return (
            <form class={'new-task-form'}>
                <input type='text' name="text" value={this.state.text} placeholder="New task details"></input>
                <select ></select>        
                <input type='submit'></input>
            </form>
        )
    }
}

export default Form
