import React, { Component } from 'react';

class Form extends Component {
  state = {};

  newTaskHandler = () => {};

  render() {
    return (
      <form className='new-task-form' onSubmit={this.props.handleSubmit}>
        <input
          placeholder='New Task Details'
          type='text'
          value={''}
          onChange={this.newTaskHandler}></input>
        <select>
          <option>Code</option>
          <option>Food</option>
          <option>Money</option>
          <option>Misc</option>
        </select>
        <input type='submit' value='Add Task' />
      </form>
    );
  }
}

export default Form;
