import React from 'react';
import './App.css';
import CategoryContainer from './Containers/CategoryContainer';
import TaskContainer from './Containers/TaskContainer';
import { CATEGORIES } from './data';

class App extends React.Component {
  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food',
      },
      {
        text: 'Save a tenner',
        category: 'Money',
      },
      {
        text: 'Build a todo app',
        category: 'Code',
      },
      {
        text: 'Build todo API',
        category: 'Code',
      },
      {
        text: 'Get an ISA',
        category: 'Money',
      },
      {
        text: 'Cook rice',
        category: 'Food',
      },
      {
        text: 'Tidy house',
        category: 'Misc',
      },
    ],
  };

  // filteredTasks = () => {
  //   const newTasksArray = [...this.state.tasks];
  //   newTasksArray.filter(task => )
  // };

  categoryFilter = (e) => {
    console.log('Working! ', e.target.textContent);
  };

  render() {
    return (
      <div className='App'>
        <h2>My tasks</h2>
        <CategoryContainer
          categories={CATEGORIES}
          filter={this.categoryFilter}
        />
        <TaskContainer tasks={this.state.tasks} />
      </div>
    );
  }
}

export default App;
