import React from 'react'
import Task from '../components/Task'

export default class TasksContainer extends React.Component {

    renderTasks = () => {
        return this.props.tasks.map(task => <Task key={task.text} category={task.category} text={task.text} />)
    }

    render(){
        return(
            <div className="tasks">
                <h5>Tasks</h5>
                {this.renderTasks()}
            </div>
        )
    }

}
