import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
import TasksContainer from './containers/TasksContainer'
import CategoryContainer from './containers/CategoryContainer';

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ],
    categories: CATEGORIES,
    currentCategory: "All",
    filteredTasks: []
  }

  componentDidMount(){
    // console.log(CATEGORIES)
  }

  categorySelection = (category) => {
    console.log(category.name)

    if(category.name !== "All"){
      let newArray = this.state.tasks
      let filtered = newArray.filter(task => task.category === this.state.currentCategory)
      this.setState(()=>({
        currentCategory: category.name,
        filteredTasks: filtered
      }))
    } else if(category.name === "All") {
      this.setState(()=>({
        currentCategory: "All"
      }))
    }

  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <h5>Category Filters</h5>
        <CategoryContainer cats={this.state.categories} catSelectHandler={this.categorySelection} selectedCategory={this.state.currentCategory} />
        <TasksContainer tasks={this.state.currentCategory === "All" ? this.state.tasks : this.state.filteredTasks} />
      </div>
    );
  }
}


export default App;


