import React from 'react';
import './App.css';
import CategoryContainer from './Containers/CategoryContainer';
import TaskContainer from './Containers/TaskContainer';
import { CATEGORIES } from './data';

class App extends React.Component {
  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food',
      },
      {
        text: 'Save a tenner',
        category: 'Money',
      },
      {
        text: 'Build a todo app',
        category: 'Code',
      },
      {
        text: 'Build todo API',
        category: 'Code',
      },
      {
        text: 'Get an ISA',
        category: 'Money',
      },
      {
        text: 'Cook rice',
        category: 'Food',
      },
      {
        text: 'Tidy house',
        category: 'Misc',
      },
    ],
    selectedButton: 'All',
  };

  buttonSelector = (e) => {
    e.persist();
    this.setState(() => ({
      selectedButton: e.target.textContent,
    }));
  };

  categoryFilter = () => {
    if (this.state.selectedButton === 'All') {
      return this.state.tasks;
    }
    return this.state.tasks.filter(
      (task) => task.category === this.state.selectedButton
    );
  };

  render() {
    console.log(this.categoryFilter());
    return (
      <div className='App'>
        <h2>My tasks</h2>
        <CategoryContainer
          categories={CATEGORIES}
          buttonSelector={this.buttonSelector}
          selectedButton={this.state.selectedButton}
        />
        <TaskContainer tasks={this.categoryFilter()} />
      </div>
    );
  }
}

export default App;
