import React, { Component } from 'react';

class Category extends Component {
  state = {
    selected: false,
  };

  clickHandler = (e) => {
    let currentSelect = document.getElementsByClassName('selected');
    console.log(currentSelect[0]);
    if (currentSelect[0] === undefined) {
      const currentState = this.state.selected;
      this.setState({ selected: !currentState });
    } else {
      currentSelect[0].className.remove('selected');
      const currentState = this.state.selected;
      this.setState({ selected: !currentState });
    }
  };

  render() {
    console.log(this.props.active);
    const { data, active, activeButton } = this.props;
    return (
      <button
        className={this.state.selected ? 'selected' : null}
        onClick={this.clickHandler}>
        {data}
      </button>
    );
  }
}

export default Category;
