import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
import TasksContainer from './containers/TasksContainer'
import CategoryContainer from './containers/CategoryContainer';

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ]
  }

  componentDidMount(){
    console.log(CATEGORIES)
  }

  categorySelection = (event) => {
    event.persist()
    console.log(`${event.target.innerText} in app!`)

  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <CategoryContainer cats={CATEGORIES} catSelectHandler={this.categorySelection} />
        <TasksContainer />
      </div>
    );
  }
}


export default App;


