import React from 'react';
import './App.css';
import CategoryContainer from './Containers/CategoryContainer';
import TaskContainer from './Containers/TaskContainer';
import { CATEGORIES } from './data';

class App extends React.Component {
  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food',
      },
      {
        text: 'Save a tenner',
        category: 'Money',
      },
      {
        text: 'Build a todo app',
        category: 'Code',
      },
      {
        text: 'Build todo API',
        category: 'Code',
      },
      {
        text: 'Get an ISA',
        category: 'Money',
      },
      {
        text: 'Cook rice',
        category: 'Food',
      },
      {
        text: 'Tidy house',
        category: 'Misc',
      },
    ],
    active: false,
  };

  // filteredTasks = () => {
  //   const newTasksArray = [...this.state.tasks];
  //   return newTasksArray.filter((task) => task.category === );
  // };

  categoryFilter = (e) => {
    // console.log('Working! ', this.state.tasks);
    const newTasksArray = [...this.state.tasks];
    const filteredArray = newTasksArray.filter(
      (task) => task.category === e.target.textContent
    );
    return filteredArray;
    // if (e.target.textContent === this.state.tasks.category) {
    //   console.log(e.target.textContent);
    // }
  };

  render() {
    // console.log(this.filteredTasks());
    return (
      <div className='App'>
        <h2>My tasks</h2>
        <CategoryContainer
          categories={this.categoryFilter()}
          filter={this.categoryFilter}
        />
        <TaskContainer tasks={this.state.tasks} />
      </div>
    );
  }
}

export default App;
